mapper.select:
SELECT productNum , productTitle , productPrice , productGrade , productPic , wish_count FROM ( SELECT p.product_num as "productNum" ,p.product_title as "productTitle" ,p.product_price as "productPrice" ,p.product_grade as "productGrade" ,p.product_pic as "productPic" ,count(c.member_id) as "wish_count" FROM product p join wish w ON (p.product_num = w.product_num) join wish c on (p.product_num = c.product_num)
WHERE w.member_id = #{memberId}
GROUP BY p.product_num ORDER BY p.product_num DESC ) A LIMIT ${startRow},${pageSize}

SELECT COUNT(DISTINCT product_num) 
FROM wish

WHERE member_id = #{memberId}

SELECT count(product_num)
FROM wish
WHERE product_num = #{productNum} and member_id = #{memberId}
